public class Game {

    private final Map map;
    private final PipeQueue pipeQueue;
    private final DelayBar delayBar;
    private final CellStack cellStack = new CellStack();

    private int numOfSteps = 0;

    public Game(int rows, int cols) {
        this(rows, cols, 0, null, null);
    }

    public Game(int rows, int cols, int delay, Cell[][] cells, List<Pipe> pipes) {
        map = new Map(rows, cols, cells);
        pipeQueue = new PipeQueue(pipes);
        delayBar = new DelayBar(delay);
    }

    static Game fromString(int rows, int cols, int delay, String cellsRep, List<Pipe> pipes) {
        Cell[][] cells = Deserializer.parseString(rows, cols, cellsRep);

        return new Game(rows, cols, delay, cells, pipes);
    }

    public boolean placePipe(int row, char col) {
        Coordinate coord = new Coordinate(row, col - 'A');
        if (map.canPlacePipe(coord)) {
            map.placePipe(coord, pipeQueue.getCurrentPipe());
            cellStack.push(coord);
            numOfSteps++;
            return true;
        }
        return false;
    }

    public void skipPipe() {
        pipeQueue.skip();
    }

    public boolean undoStep() {
        if (cellStack.isEmpty()) {
            return false;
        }
        Coordinate coord = cellStack.pop();
        map.removePipe(coord);
        numOfSteps--;
        return true;
    }

    public void display() {
        map.display();
        System.out.println();
        pipeQueue.display();
        cellStack.display();
        System.out.println();
        delayBar.display();
    }

    public void updateState() {
        map.fillTiles(delayBar.getDistance());
    }

    public boolean hasWon() {
        return map.isWaterAt(map.getSinkCell().getCoordinate());
    }

    public boolean hasLost() {
        return map.hasLost();
    }

    public int getNumOfSteps() {
        return numOfSteps;
    }
}
