package game.pipes;

import game.MapElement;
import util.Direction;
import util.PipePatterns;

import java.util.Arrays;
import java.util.LinkedList;
import java.util.List;
import java.util.Random;

public class Pipe implements MapElement {

    private final Shape shape;
    private boolean filled = false;

    public Pipe(Shape shape) {
        this.shape = shape;
    }

    public void setFilled() {
        filled = true;
    }

    public boolean getFilled() {
        return filled;
    }

    public Direction[] getConnections() {
        return shape.getConnections();
    }

    @Override
    public char toSingleChar() {
        return shape.getCharByState(filled);
    }

    public static Pipe fromString(String rep) {
        for (Shape shape : Shape.values()) {
            if (shape.name().equals(rep)) {
                return new Pipe(shape);
            }
        }
        throw new IllegalArgumentException("Invalid pipe shape: " + rep);
    }

    public enum Shape {
        HORIZONTAL(new Direction[]{Direction.RIGHT}, PipePatterns.Filled.HORIZONTAL, PipePatterns.Unfilled.HORIZONTAL),
        VERTICAL(new Direction[]{Direction.DOWN}, PipePatterns.Filled.VERTICAL, PipePatterns.Unfilled.VERTICAL),
        TOP_LEFT(new Direction[]{Direction.RIGHT, Direction.DOWN}, PipePatterns.Filled.TOP_LEFT, PipePatterns.Unfilled.TOP_LEFT),
        TOP_RIGHT(new Direction[]{Direction.LEFT, Direction.DOWN}, PipePatterns.Filled.TOP_RIGHT, PipePatterns.Unfilled.TOP_RIGHT),
        BOTTOM_LEFT(new Direction[]{Direction.RIGHT, Direction.UP}, PipePatterns.Filled.BOTTOM_LEFT, PipePatterns.Unfilled.BOTTOM_LEFT),
        BOTTOM_RIGHT(new Direction[]{Direction.LEFT, Direction.UP}, PipePatterns.Filled.BOTTOM_RIGHT, PipePatterns.Unfilled.BOTTOM_RIGHT),
        CROSS(new Direction[]{Direction.LEFT, Direction.RIGHT, Direction.UP, Direction.DOWN}, PipePatterns.Filled.CROSS, PipePatterns.Unfilled.CROSS);

        final Direction[] connections;
        final char filledChar;
        final char unfilledChar;

        Shape(Direction[] connections, char filled, char unfilled) {
            this.connections = connections;
            this.filledChar = filled;
            this.unfilledChar = unfilled;
        }

        public Direction[] getConnections() {
            return connections.clone();
        }

        public char getCharByState(boolean isFilled) {
            return isFilled ? filledChar : unfilledChar;
        }
    }
}


package game;

import game.pipes.Pipe;
