package game;

import game.pipes.Pipe;
import game.pipes.Pipe.Shape;

import java.io.*;
import java.util.*;

public class PipeQueue {

    private static final int MAX_GEN_LENGTH = 5;

    private final LinkedList<Pipe> pipeQueue;

    PipeQueue() {
        pipeQueue = new LinkedList<>();
        generatePipes(MAX_GEN_LENGTH);
    }

    PipeQueue(List<Pipe> pipes) {
        pipeQueue = new LinkedList<>(pipes);
        generatePipes(MAX_GEN_LENGTH - pipes.size());
    }

    Pipe peek() {
        if (pipeQueue.isEmpty()) {
            throw new IllegalStateException("No pipes in the queue.");
        }
        return pipeQueue.getFirst();
    }

    void consume() {
        pipeQueue.removeFirst();
        generatePipes(1);
    }

    void undo(final Pipe pipe) {
        pipeQueue.addFirst(pipe);
    }

    void display() {
        System.out.print("Next Pipes:  ");
        for (Pipe p : pipeQueue) {
            System.out.print(p.toSingleChar() + "    ");
        }
        System.out.println();
    }

    private void generatePipes(int count) {
        Random random = new Random();
        for (int i = 0; i < count; i++) {
            pipeQueue.addLast(generateNewPipe());
        }
    }

    private static Pipe generateNewPipe() {
        Random random = new Random();
        Shape[] shapes = Shape.values();
        return new Pipe(shapes[random.nextInt(shapes.length)]);
    }
}

