    public Map(int rows, int cols) {
        this.rows = rows;
        this.cols = cols;
        this.cells = new Cell[rows][cols];

        for (int r = 0; r < rows; ++r) {
            for (int c = 0; c < cols; ++c) {
                Coordinate coord = new Coordinate(r, c);
                if (r == 0 || r == rows - 1 || c == 0 || c == cols - 1) {
                    cells[r][c] = new TerminationCell(coord, Direction.NONE, TerminationCell.Type.SINK);
                    if (sinkCell == null) {
                        sinkCell = (TerminationCell) cells[r][c];
                    }
                } else {
                    cells[r][c] = new FillableCell(coord);
                }
            }
        }

        sourceCell = (TerminationCell) cells[rows / 2][cols / 2];
        sourceCell.type = TerminationCell.Type.SOURCE;
    }

    /**
     * Creates a map with the given cells.
     *
     * <p>
     * You should make sure that the map generated adheres to the specifications as stated in the README.
     * </p>
     *
     * @param rows  Number of rows.
     * @param cols  Number of columns.
     * @param cells Cells to fill the map.
     */
    public Map(int rows, int cols, Cell[][] cells) {
        this.rows = rows;
        this.cols = cols;
        this.cells = cells;

        for (int r = 0; r < rows; ++r) {
            for (int c = 0; c < cols; ++c) {
                if (cells[r][c] instanceof TerminationCell) {
                    if (((TerminationCell) cells[r][c]).type == TerminationCell.Type.SOURCE) {
                        sourceCell = (TerminationCell) cells[r][c];
                    } else if (((TerminationCell) cells[r][c]).type == TerminationCell.Type.SINK) {
                        sinkCell = (TerminationCell) cells[r][c];
                    }
                }
            }
        }
    }

