        }

        sourceCell = sourceCells.get(0);
        sinkCell = sinkCells.get(0);
    }

    private boolean isValidCoordinate(Coordinate coord) {
        return coord.row >= 1 && coord.row <= rows && coord.col >= 1 && coord.col <= cols;
    }
