    public void fillTiles(int distance) {
        if (prevFilledDistance != null && prevFilledDistance == distance) {
            return;
        }
        prevFilledDistance = distance;
        for (int r = 0; r < rows; r++) {
            for (int c = 0; c < cols; c++) {
                if (cells[r][c] instanceof FillableCell) {
                    FillableCell cell = (FillableCell) cells[r][c];
                    if (filledTiles.contains(cell.coord)) {
                        continue;
                    }
                    if (cell.getPipe().isPresent()) {
                        filledTiles.add(cell.coord);
                        cell.getPipe().get().setFilled();
                    }
                }
            }
        }
