        this.cols = cols;
        this.cells = new Cell[rows][cols];
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (i == 0 || i == rows - 1 || j == 0 || j == cols - 1) {
                    cells[i][j] = new Wall(new Coordinate(i, j));
                } else {
                    cells[i][j] = new FillableCell(new Coordinate(i, j));
                }
