        sourceCell = new TerminationCell(new Coordinate(0, 0), Direction.RIGHT, TerminationCell.Type.SOURCE);
        sinkCell = new TerminationCell(new Coordinate(rows - 1, cols - 1), Direction.LEFT, TerminationCell.Type.SINK);
        cells[0][0] = sourceCell;
