        this.cols = cols;
        this.cells = cells;
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (cells[i][j] instanceof TerminationCell) {
                    if (((TerminationCell) cells[i][j]).type == TerminationCell.Type.SOURCE) {
                        sourceCell = (TerminationCell) cells[i][j];
                    } else {
                        sinkCell = (TerminationCell) cells[i][j];
                    }
                }
            }
        }
