    boolean tryPlacePipe(int row, int col, Pipe p) {
        if (row < 0 || row >= rows || col < 0 || col >= cols) {
            return false;
        }
        if (cells[row][col] instanceof FillableCell) {
            FillableCell cell = (FillableCell) cells[row][col];
            if (cell.getPipe().isEmpty()) {
                cell.setPipe(p);
                return true;
            }
        }
        return false;
