     *
     * @param row Row number, 1-based.
     * @param col Column character.
     * @return {@code true} if the pipe is placed.
     */
    public boolean placePipe(int row, char col) {
        Coordinate coord = new Coordinate(row - 1, col - 'A');
        Optional<Pipe> pipe = pipeQueue.getNextPipe();

        if (pipe.isPresent() && map.placePipe(coord, pipe.get())) {
            cellStack.push(new FillableCell(coord, pipe.get()));
            pipeQueue.removeNextPipe();
            delayBar.decrement();
            numOfSteps++;
            return true;
        }
        return false;
