```java
package pa1.model;

import pa1.model.Cell;
import pa1.model.Direction;
import pa1.model.EntityCell;
import pa1.model.ExtraLife;
import pa1.model.Gem;
import pa1.model.Mine;
import pa1.model.Player;
import pa1.model.Position;
import pa1.model.StopCell;

import java.util.*;

public class GameBoard {

    private final int numRows;
    private final int numCols;
    private final Cell[][] board;
    private final Player player;

    public GameBoard(final int numRows, final int numCols, final Cell[][] cells) {
        this.numRows = numRows;
        this.numCols = numCols;
        this.board = cells;
        this.player = getPlayer(cells);
        validateBoard();
    }

    private Player getPlayer(Cell[][] cells) {
        for (Cell[] row : cells) {
            for (Cell cell : row) {
                if (cell.getEntity() instanceof Player) {
                    return (Player) cell.getEntity();
                }
            }
        }
        return null;
    }

    private void validateBoard() {
        Set<Cell> visited = new HashSet<>();
        Queue<Cell> queue = new LinkedList<>();
        queue.add(getCell(player.getPosition()));
        while (!queue.isEmpty()) {
            Cell current = queue.poll();
            if (visited.contains(current)) {
                continue;
            }
            visited.add(current);
            if (current.getEntity() instanceof Gem) {
                continue;
            }
            for (Direction direction : Direction.values()) {
                Position nextPosition = current.getPosition().move(direction);
                if (isInside(nextPosition)) {
                    Cell nextCell = getCell(nextPosition);
                    if (!visited.contains(nextCell)) {
                        queue.add(nextCell);
                    }
                }
            }
        }
        if (visited.size() != numRows * numCols) {
            throw new IllegalArgumentException("Some cells are not reachable");
        }
    }

    public Cell[] getRow(final int row) {
        return board[row];
    }

    public Cell[] getCol(final int col) {
        Cell[] colCells = new Cell[numRows];
        for (int i = 0; i < numRows; i++) {
            colCells[i] = board[i][col];
        }
        return colCells;
    }

    public Cell getCell(final int row, final int col) {
        return board[row][col];
    }

    public Cell getCell(final Position position) {
        return getCell(position.getRow(), position.getCol());
    }

    public EntityCell getEntityCell(final int row, final int col) {
        Cell cell = getCell(row, col);
        if (!(cell instanceof EntityCell)) {
            throw new IllegalArgumentException("Cell is not an EntityCell");
        }
        return (EntityCell) cell;
    }

    private boolean isInside(Position position) {
        return position.getRow() >= 0 && position.getRow() < numRows && position.getCol() >= 0 && position.getCol() < numCols;
    }
}

