```java
package pa1.controller;

import pa1.model.Cell;
import pa1.model.Direction;
import pa1.model.EntityCell;
import pa1.model.ExtraLife;
import pa1.model.GameBoard;
import pa1.model.Gem;
import pa1.model.Mine;
import pa1.model.MoveResult;
import pa1.model.Position;
import pa1.model.StopCell;

public class GameBoardController {

    private final GameBoard gameBoard;

    public GameBoardController(final GameBoard gameBoard) {
        this.gameBoard = gameBoard;
    }

    public MoveResult makeMove(final Direction direction) {
        Position nextPosition = gameBoard.player.getPosition().move(direction);
        Cell nextCell = gameBoard.getCell(nextPosition);
        if (nextCell instanceof StopCell) {
            return MoveResult.STOP;
        } else if (nextCell instanceof Mine) {
            gameBoard.player.die();
            return MoveResult.DEAD;
        } else if (nextCell instanceof Gem) {
            gameBoard.player.pickUpGem();
            nextCell.setEntity(null);
            return MoveResult.GEM;
        } else if (nextCell instanceof ExtraLife) {
            gameBoard.player.addLife();
            nextCell.setEntity(null);
            return MoveResult.EXTRA_LIFE;
        } else {
            gameBoard.player.move(direction);
            return MoveResult.MOVE;
        }
    }

    public void undoMove(final MoveResult prevMove) {
        Direction direction = prevMove.getDirection();
        if (direction == null) {
            return;
        }
        direction = direction.getOpposite();
        makeMove(direction);
    }
}

