public class GameBoard {

    /**
     * Number of rows in the game board.
     */
    private final int numRows;
    /**
     * Number of columns in the game board.
     */
    private final int numCols;

    /**
     * 2D array representing each cell in the game board.
     */
    private final Cell[][] board;

    /**
     * The instance of {@link Player} on this game board.
     */
    private final Player player;

    /**
     * Creates an instance using the provided creation parameters.
     *
     * @param numRows The number of rows in the game board.
     * @param numCols The number of columns in the game board.
     * @param cells   The initial values of cells.
     * @throws IllegalArgumentException if any of the following are true:
     *                                  <ul>
     *                                      <li>{@code numRows} is not equal to {@code cells.length}</li>
     *                                      <li>{@code numCols} is not equal to {@code cells[0].length}</li>
     *                                      <li>There is no player or more than one player in {@code cells}</li>
     *                                      <li>There are no gems in {@code cells}</li>
     *                                      <li>There are some gems which cannot be reached by the player</li>
     *                                  </ul>
     */
    public GameBoard(final int numRows, final int numCols, final Cell[][] cells) {
        if (numRows != cells.length || numCols != cells[0].length) {
            throw new IllegalArgumentException("The dimensions of the cells array must match the specified dimensions.");
        }

        this.numRows = numRows;
        this.numCols = numCols;
        this.board = cells;

        int playerCount = 0;
        for (int row = 0; row < numRows; row++) {
            for (int col = 0; col < numCols; col++) {
                Cell cell = cells[row][col];
                if (cell instanceof Player) {
                    playerCount++;
                    this.player = (Player) cell;
                }
            }
        }

        if (playerCount == 0) {
            throw new IllegalArgumentException("There must be at least one player on the game board.");
        }

        // Check if all gems are reachable by the player
        Set<Position> reachablePositions = new HashSet<>();
        Queue<Position> positionsToCheck = new LinkedList<>();
        positionsToCheck.add(player.getPosition());
        while (!positionsToCheck.isEmpty()) {
            Position position = positionsToCheck.poll();
            if (!reachablePositions.add(position)) {
                continue;
            }

            for (Direction direction : Direction.values()) {
                Position newPosition = position.offset(direction.getOffset());
                if (newPosition.isValid(numRows, numCols) && !reachablePositions.contains(newPosition)) {
                    Cell cell = cells[newPosition.getRow()][newPosition.getCol()];
                    if (cell instanceof Gem || cell instanceof StopCell) {
                        positionsToCheck.add(newPosition);
                    }
                }
            }
        }

        for (int row = 0; row < numRows; row++) {
            for (int col = 0; col < numCols; col++) {
                Cell cell = cells[row][col];
                if (cell instanceof Gem && !reachablePositions.contains(cell.getPosition())) {
                    throw new IllegalArgumentException("There are gems which cannot be reached by the player.");
                }
            }
        }
    }

