            List<Position> collectedGems = gameBoard.collectGems(newPosition);
            List<Position> collectedExtraLives = gameBoard.collectExtraLives(newPosition);
            return new MoveResult.Valid.Alive(currentPosition, newPosition, collectedGems, collectedExtraLives);
