```java
public class GameBoard {

    private final int numRows;
    private final int numCols;
    private final Cell[][] board;
    private final Player player;

    public GameBoard(final int numRows, final int numCols, final Cell[][] cells) {
        if (numRows != cells.length || numCols != cells[0].length) {
            throw new IllegalArgumentException("Number of rows and columns do not match the size of the cells array.");
        }

        this.numRows = numRows;
        this.numCols = numCols;
        this.board = cells;

        Player player = null;
        int gemCount = 0;

        for (int row = 0; row < numRows; row++) {
            for (int col = 0; col < numCols; col++) {
                if (cells[row][col] instanceof Player) {
                    if (player != null) {
                        throw new IllegalArgumentException("There is more than one player in the cells array.");
                    }
                    player = (Player) cells[row][col];
                } else if (cells[row][col] instanceof Gem) {
                    gemCount++;
                }
            }
        }

        if (player == null) {
            throw new IllegalArgumentException("There is no player in the cells array.");
        }

        if (gemCount == 0) {
            throw new IllegalArgumentException("There are no gems in the cells array.");
        }

        this.player = player;
    }

    public Cell[] getRow(final int row) {
        return board[row];
    }

    public Cell[] getCol(final int col) {
        Cell[] column = new Cell[numRows];
        for (int row = 0; row < numRows; row++) {
            column[row] = board[row][col];
        }
        return column;
    }

    public Cell getCell(final int row, final int col) {
        return board[row][col];
    }

    public Cell getCell(final Position position) {
        return getCell(position.row(), position.col());
    }

    public EntityCell getEntityCell(final int row, final int col) {
        Cell cell = getCell(row, col);
        if (cell instanceof EntityCell) {
            return (EntityCell) cell;
        } else {
            throw new IllegalArgumentException("The cell at the specified position is not an instance of EntityCell.");
        }
    }

    public EntityCell getEntityCell(final Position position) {
        return getEntityCell(position.row(), position.col());
    }

    public int getNumRows() {
        return numRows;
    }

    public int getNumCols() {
        return numCols;
    }

    public Player getPlayer() {
        return player;
    }

    public int getNumGems() {
        int gemCount = 0;
        for (int row = 0; row < numRows; row++) {
            for (int col = 0; col < numCols; col++) {
                if (board[row][col] instanceof Gem) {
                    gemCount++;
                }
            }
        }
        return gemCount;
    }
}
