    public MoveResult makeMove(final Direction direction) {
        Position playerPosition = gameBoard.getPlayerPosition();
        Position nextPosition = playerPosition.nextPosition(direction);
        Cell nextCell = gameBoard.getCell(nextPosition);

        if (nextCell instanceof EntityCell) {
            EntityCell nextEntityCell = (EntityCell) nextCell;
            if (nextEntityCell.getEntity() instanceof Gem) {
                gameBoard.setCell(playerPosition, new EntityCell(playerPosition, null));
                gameBoard.setCell(nextPosition, new EntityCell(nextPosition, gameBoard.getPlayer()));
                return MoveResult.GEM_FOUND;
            } else if (nextEntityCell.getEntity() instanceof ExtraLife) {
                gameBoard.setCell(playerPosition, new EntityCell(playerPosition, null));
                gameBoard.setCell(nextPosition, new EntityCell(nextPosition, gameBoard.getPlayer()));
                gameBoard.getPlayer().incrementLives();
                return MoveResult.EXTRA_LIFE_FOUND;
            } else if (nextEntityCell.getEntity() instanceof Mine) {
                gameBoard.setCell(playerPosition, new EntityCell(playerPosition, null));
                gameBoard.getPlayer().decrementLives();
                return MoveResult.MINE_EXPLODED;
            }
        } else if (nextCell instanceof StopCell) {
            return MoveResult.STOP_CELL_REACHED;
        }

        gameBoard.setCell(playerPosition, new EntityCell(playerPosition, null));
        gameBoard.setCell(nextPosition, new EntityCell(nextPosition, gameBoard.getPlayer()));
        return MoveResult.SUCCESS;
