     *                                  <ul>
     *                                      <li>{@code numRows} is not equal to {@code cells.length}</li>
     *                                      <li>{@code numCols} is not equal to {@code cells[0].length}</li>
     *                                      <li>There is no player or more than one player in {@code cells}</li>
     *                                      <li>There are no gems in {@code cells}</li>
     *                                      <li>There are some gems which cannot be reached by the player</li>
     *                                  </ul>
     */
    public GameBoard(final int numRows, final int numCols, final Cell[][] cells) {
        this.numRows = numRows;
        this.numCols = numCols;
        this.board = new Cell[numRows][numCols];
        Player player = null;
        int gemCount = 0;

        for (int i = 0; i < numRows; i++) {
            for (int j = 0; j < numCols; j++) {
                board[i][j] = cells[i][j];
                if (cells[i][j] instanceof Player) {
                    if (player != null) {
                        throw new IllegalArgumentException("More than one player found in the game board");
                    }
                    player = (Player) cells[i][j];
                } else if (cells[i][j] instanceof Gem) {
                    gemCount++;
                }
            }
        }

        if (player == null) {
            throw new IllegalArgumentException("No player found in the game board");
        }

        if (gemCount == 0) {
            throw new IllegalArgumentException("No gems found in the game board");
        }

        this.player = player;
    }

