        String[] lines = mapText.split("\n");
        int undoLimit = Integer.parseInt(lines[0].trim());
        if (undoLimit < -1) {
            throw new IllegalArgumentException("Undo limit cannot be negative but not -1");
        }

        Set<Position> destinations = new HashSet<>();
        Map<Position, Entity> map = new HashMap<>();
        Map<Character, Integer> playerIds = new HashMap<>();
        Map<Character, Integer> boxIds = new HashMap<>();

        for (int y = 0; y < lines.length - 1; y++) {
            String line = lines[y + 1];
            for (int x = 0; x < line.length(); x++) {
                char c = line.charAt(x);
