                        throw new IllegalArgumentException("Multiple same upper-case letters, i.e., one player can only exist at one position.");
                    }
                    int id = c - 'A';
                    playerIds.put(c, id);
                    map.put(position, new Player(id));
                } else if (Character.isLowerCase(c)) {
                    int id = c - 'a';
                    boxIds.put(c, id);
                    map.put(position, new Box(id));
                }
            }
        }

        if (playerIds.isEmpty()) {
            throw new IllegalArgumentException("There are no players in the map.");
        }

        if (boxIds.size() != destinations.size()) {
            throw new IllegalArgumentException("The number of boxes is not equal to the number of box destinations.");
        }

        for (Map.Entry<Character, Integer> entry : boxIds.entrySet()) {
            if (!playerIds.containsKey(Character.toUpperCase(entry.getKey()))) {
                throw new IllegalArgumentException("There are boxes whose getPlayerId() do not match any player on the game board.");
            }
            if (entry.getValue() != playerIds.get(Character.toUpperCase(entry.getKey()))) {
                throw new IllegalArgumentException("There are boxes whose getPlayerId() do not match any player on the game board.");
            }
        }

        for (Map.Entry<Character, Integer> entry : playerIds.entrySet()) {
            if (!boxIds.containsKey(Character.toLowerCase(entry.getKey()))) {
                throw new IllegalArgumentException("There are players that have no corresponding boxes.");
            }
        }

        return new GameMap(map, destinations, undoLimit);
    }

    public Entity getEntity(Position position) {
        return map.getOrDefault(position, new Empty());
    }

    public void putEntity(Position position, Entity entity) {
        map.put(position, entity);
    }

    public Set<Position> getDestinations() {
        return destinations;
    }

    public Optional<Integer> getUndoLimit() {
        return undoLimit == -1 ? Optional.empty() : Optional.of(undoLimit);
    }

    public Set<Integer> getPlayerIds() {
        return map.values().stream()
                .filter(entity -> entity instanceof Player)
                .map(entity -> ((Player) entity).getId())
                .collect(Collectors.toSet());
    }

    public int getMaxWidth() {
        return maxWidth;
    }

    public int getMaxHeight() {
        return maxHeight;
    }
}
