        filledTiles.add(sourceCell.getCoordinate());
        for (int i = 0; i < distance; i++) {
            Set<Coordinate> newFilledTiles = new HashSet<>();
            for (Coordinate coord : filledTiles) {
                for (Direction dir : Direction.values()) {
                    Coordinate neighbor = coord.getNeighbor(dir);
                    if (neighbor.isValid(rows, cols) && cells[neighbor.row][neighbor.col] instanceof FillableCell) {
                        FillableCell fillableCell = (FillableCell) cells[neighbor.row][neighbor.col];
                        fillableCell.setFilled();
                        newFilledTiles.add(neighbor);
