        if (cell instanceof FillableCell) {
            ((FillableCell) cell).removePipe();
        } else {
            throw new IllegalArgumentException("Cell is not an instance of FillableCell");
        }
    }

    /**
     * Fills the source cell.
     */
    public void fillBeginTile() {
        sourceCell.setFilled();
    }

