     * Fills all pipes that are within {@code distance} units from the {@code sourceCell}.
     *
     * @param distance Distance to fill pipes.
     */
    public void fillTiles(int distance) {
        if (prevFilledDistance == null || distance < prevFilledDistance) {
            prevFilledTiles = 0;
            filledTiles.clear();
            filledTiles.add(new Coordinate(sourceCell.getRow(), sourceCell.getCol()));
        }
        while (prevFilledTiles < filledTiles.size() && distance > prevFilledDistance) {
            Set<Coordinate> newFilledTiles = new HashSet<>();
            for (Coordinate coord : filledTiles) {
                for (Direction dir : Direction.values()) {
                    Coordinate newCoord = coord.getAdjacent(dir);
                    if (newCoord.isValid(rows, cols) && cells[newCoord.row][newCoord.col] instanceof FillableCell) {
                        FillableCell cell = (FillableCell) cells[newCoord.row][newCoord.col];
                        if (!cell.isFilled() && cell.canFillPipe()) {
                            cell.setFilled();
                            newFilledTiles.add(newCoord);
                            prevFilledTiles++;
                        }
                    }
                }
            }
            filledTiles.addAll(newFilledTiles);
            prevFilledDistance++;
        }
    }

    /**
     * Checks whether there exists a path from {@code sourceCell} to {@code sinkCell}.
     *
     * @return {@code true} if a path exists, else {@code false}.
     */
    public boolean checkPath() {
        Queue<Coordinate> queue = new LinkedList<>();
        Set<Coordinate> visited = new HashSet<>();
        queue.add(new Coordinate(sourceCell.getRow(), sourceCell.getCol()));
        visited.add(new Coordinate(sourceCell.getRow(), sourceCell.getCol()));

        while (!queue.isEmpty()) {
            Coordinate current = queue.poll();
            if (current.equals(new Coordinate(sinkCell.getRow(), sinkCell.getCol()))) {
                return true;
            }
            for (Direction dir : Direction.values()) {
                Coordinate next = current.getAdjacent(dir);
                if (next.isValid(rows, cols) && cells[next.row][next.col] instanceof FillableCell) {
                    FillableCell cell = (FillableCell) cells[next.row][next.col];
                    if (cell.isFilled() && !visited.contains(next)) {
                        queue.add(next);
                        visited.add(next);
                    }
                }
            }
        }
        return false;
    }

    /**
     * @return {@code true} if the game is lost.
     */
    public boolean hasLost() {
        return prevFilledTiles == 0;
    }
}
