    public void fillTiles(int distance) {
        Set<Coordinate> currentFilledTiles = new HashSet<>();
        currentFilledTiles.add(new Coordinate(sourceCell.getRow(), sourceCell.getCol()));

        while (distance > 0) {
            Set<Coordinate> nextFilledTiles = new HashSet<>();
            for (Coordinate coord : currentFilledTiles) {
                int row = coord.row;
                int col = coord.col;
                for (Direction dir : Direction.values()) {
                    int newRow = row + dir.deltaRow();
                    int newCol = col + dir.deltaCol();
                    if (newRow >= 0 && newRow < rows && newCol >= 0 && newCol < cols) {
                        Cell cell = cells[newRow][newCol];
                        if (cell instanceof FillableCell) {
                            FillableCell fillableCell = (FillableCell) cell;
                            if (!fillableCell.isFilled()) {
                                fillableCell.setFilled();
                                nextFilledTiles.add(new Coordinate(newRow, newCol));
