    public boolean checkPath() {
        Queue<Coordinate> queue = new LinkedList<>();
        Set<Coordinate> visited = new HashSet<>();
        queue.add(new Coordinate(sourceCell.getRow(), sourceCell.getCol()));

        while (!queue.isEmpty()) {
            Coordinate current = queue.poll();
            if (current.equals(new Coordinate(sinkCell.getRow(), sinkCell.getCol()))) {
                return true;
            }
            for (Direction dir : Direction.values()) {
                int newRow = current.row + dir.deltaRow();
                int newCol = current.col + dir.deltaCol();
                Coordinate neighbor = new Coordinate(newRow, newCol);
                if (newRow >= 0 && newRow < rows && newCol >= 0 && newCol < cols
                        && cells[newRow][newCol] instanceof FillableCell
