        // Initialize the map with empty cells
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (i == 0 || i == rows - 1 || j == 0 || j == cols - 1) {
                    cells[i][j] = new Wall();
                } else {
                    cells[i][j] = new FillableCell();
                }
            }
        }
