        filledTiles.add(new Coordinate(sourceCell.getRow(), sourceCell.getCol()));
        for (int i = 0; i < distance; i++) {
            Set<Coordinate> newFilledTiles = new HashSet<>();
            for (Coordinate coord : filledTiles) {
                for (Direction dir : Direction.values()) {
                    Coordinate neighborCoord = coord.getNeighbor(dir);
                    if (neighborCoord.isValid(rows, cols)) {
                        Cell neighborCell = cells[neighborCoord.row][neighborCoord.col];
                        if (neighborCell instanceof FillableCell) {
                            FillableCell fillableCell = (FillableCell) neighborCell;
                            fillableCell.setFilled();
                            newFilledTiles.add(neighborCoord);
                        }
                    }
                }
            }
            filledTiles.addAll(newFilledTiles);
        }
    }

