/**
 * Map of the game.
 */
public class Map {

    private final int rows;
    private final int cols;
    public final Cell[][] cells;

    private TerminationCell sourceCell;
    private TerminationCell sinkCell;

    // ... (other variables and constructors)

    public Map(int rows, int cols) {
        this.rows = rows;
        this.cols = cols;
        this.cells = new Cell[rows][cols];
    }

    public Map(int rows, int cols, Cell[][] cells) {
        this.rows = rows;
        this.cols = cols;
        this.cells = cells;
        // Initialize sourceCell and sinkCell
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                if (cells[i][j] instanceof TerminationCell) {
                    TerminationCell terminationCell = (TerminationCell) cells[i][j];
                    if (terminationCell.isSource()) {
                        sourceCell = terminationCell;
                    } else if (terminationCell.isSink()) {
                        sinkCell = terminationCell;
                    }
                }
            }
        }
    }

    // ... (other methods)

    boolean tryPlacePipe(int row, int col, Pipe p) {
        if (row < 0 || row >= rows || col < 0 || col >= cols) {
            return false;
        }
        Cell cell = cells[row][col];
        if (cell instanceof FillableCell) {
            FillableCell fillableCell = (FillableCell) cell;
            if (!fillableCell.isOccupied()) {
                fillableCell.setPipe(p);
                return true;
            }
        }
        return false;
    }

    // ... (other methods)

    public void undo(final Coordinate coord) {
        Cell cell = cells[coord.row][coord.col];
        if (cell instanceof FillableCell) {
            FillableCell fillableCell = (FillableCell) cell;
            fillableCell.removePipe();
        } else {
            throw new IllegalArgumentException("Cell is not an instance of FillableCell");
        }
    }

    public void fillBeginTile() {
        sourceCell.setFilled();
    }

    public void fillTiles(int distance) {
        Set<Coordinate> filledTiles = new HashSet<>();
        filledTiles.add(new Coordinate(sourceCell.getRow(), sourceCell.getCol()));
        for (int i = 0; i < distance; i++) {
            Set<Coordinate> newFilledTiles = new HashSet<>();
            for (Coordinate coord : filledTiles) {
                int row = coord.row;
                int col = coord.col;
                for (Direction dir : Direction.values()) {
                    int newRow = row + dir.deltaRow();
                    int newCol = col + dir.deltaCol();
                    if (newRow >= 0 && newRow < rows && newCol >= 0 && newCol < cols) {
                        Cell cell = cells[newRow][newCol];
                        if (cell instanceof FillableCell) {
                            FillableCell fillableCell = (FillableCell) cell;
                            if (!fillableCell.isOccupied()) {
                                fillableCell.setFilled();
                                newFilledTiles.add(new Coordinate(newRow, newCol));
                            }
                        }
                    }
                }
            }
            filledTiles.addAll(newFilledTiles);
        }
    }

    public boolean checkPath() {
        Queue<Coordinate> queue = new LinkedList<>();
        Set<Coordinate> visited = new HashSet<>();
        queue.add(new Coordinate(sourceCell.getRow(), sourceCell.getCol()));
        while (!queue.isEmpty()) {
            Coordinate coord = queue.poll();
            int row = coord.row;
            int col = coord.col;
            if (row == sinkCell.getRow() && col == sinkCell.getCol()) {
                return true;
            }
            for (Direction dir : Direction.values()) {
                int newRow = row + dir.deltaRow();
                int newCol = col + dir.deltaCol();
                if (newRow >= 0 && newRow < rows && newCol >= 0 && newCol < cols) {
                    Cell cell = cells[newRow][newCol];
                    if (cell instanceof FillableCell) {
                        FillableCell fillableCell = (FillableCell) cell;
                        if (fillableCell.isOccupied()) {
                            queue.add(new Coordinate(newRow, newCol));
                            visited.add(new Coordinate(newRow, newCol));
                        }
                    }
                }
            }
        }
        return false;
    }

    public boolean hasLost() {
        return prevFilledTiles == filledTiles.size();
    }
}
