        // Check moves in the same column
        for (int r = 0; r < size; r++) {
            if (r != row) {
                Place target = new Place(r, col);
                if (isPathClear(game, source, target)) {
                    validMoves.add(new Move(source, target));
                }
            }
        }

        return validMoves.toArray(new Move[0]);
    }

    /**
     * Check if the path between source and target is clear for an Archer move
     *
     * @param game   the game object
     * @param source the source place
     * @param target the target place
     * @return true if the path is clear, false otherwise
     */
    private boolean isPathClear(Game game, Place source, Place target) {
        Configuration configuration = game.getConfiguration();
        int size = configuration.getSize();
        int row1 = source.getRow();
        int col1 = source.getColumn();
        int row2 = target.getRow();
        int col2 = target.getColumn();

        if (row1 == row2) {
            int start = Math.min(col1, col2) + 1;
            int end = Math.max(col1, col2);
            for (int c = start; c < end; c++) {
                if (configuration.getPiece(new Place(row1, c)) != null) {
                    return false;
