        } else if (col1 == col2) {
            int start = Math.min(row1, row2) + 1;
            int end = Math.max(row1, row2);
            for (int r = start; r < end; r++) {
                if (configuration.getPiece(new Place(r, col1)) != null) {
                    return false;
                }
            }
        }

        return true;
    }
