        Position playerPosition = gameBoard.getPlayerPosition();
        Position newPosition = playerPosition.move(direction);
        Cell newCell = gameBoard.getCell(newPosition);

        if (newCell instanceof EntityCell) {
            MoveResult moveResult = ((EntityCell) newCell).interact();
            if (moveResult.isSuccess()) {
                gameBoard.movePlayerTo(newPosition);
            }
            return moveResult;
        } else {
            return MoveResult.failed("Invalid move");
        }
    }

    /**
     * Undoes a move by reverting all changes performed by the specified move.
     *
     * <p>
     * Hint: Undoing a move is effectively the same as reversing everything you have done to make a move.
     * </p>
     *
     * @param prevMove The {@link MoveResult} object to revert.
     */
    public void undoMove(final MoveResult prevMove) {
        if (prevMove.isSuccess()) {
            gameBoard.movePlayerTo(prevMove.getPreviousPosition());
            if (prevMove.getInteractedCell() instanceof Gem) {
                gameBoard.removeGem(prevMove.getInteractedCell().getPosition());
            } else if (prevMove.getInteractedCell() instanceof ExtraLife) {
                gameBoard.removeExtraLife(prevMove.getInteractedCell().getPosition());
            }
        }
    }
}
