        int undoLimit = Integer.parseInt(lines[0].trim());
        Set<Position> destinations = new HashSet<>();
        Map<Position, Entity> map = new HashMap<>();
        AtomicInteger playerCount = new AtomicInteger(0);
        Map<Character, Position> playerPositions = new HashMap<>();
        Map<Character, Box> boxes = new HashMap<>();

