    private void undoDestinations(Transition transition) {
        Set<Position> destinationSet = transition.getDestinations();
        destinations.clear();
        destinations.addAll(destinationSet);
    }

    private void undoUndoQuota(Transition transition) {
        undoQuota = transition.getUndoQuota();
    }

