        map.sinkCell = new TerminationCell(new Coordinate(rows + 1, cols + 1), TerminationCell.Type.SINK);

        // Initialize the first pipe
        Pipe pipe = Pipe.fromString("HHH");
        map.tryPlacePipe(new Coordinate(2, 2), pipe);
        pipeQueue.consume();
    }

    /**
     * Creates a game with a given map and various properties.
