     * </p>
     *
     * @param distance Distance to fill pipes.
     */
    public void fillTiles(int distance) {
        if (distance == 0) {
            fillBeginTile();
            filledTiles.add(sourceCell.coord);
            prevFilledTiles = 1;
            prevFilledDistance = 0;
            return;
        }

        if (prevFilledDistance != null && distance <= prevFilledDistance) {
            return;
        }

        Set<Coordinate> newFilledTiles = new HashSet<>(filledTiles);

        for (Coordinate coord : filledTiles) {
            Cell cell = cells[coord.row][coord.col];
            if (cell instanceof FillableCell) {
                FillableCell fillableCell = (FillableCell) cell;
                if (fillableCell.getPipe().isPresent()) {
                    Pipe pipe = fillableCell.getPipe().get();
                    for (Direction direction : pipe.getConnections()) {
                        Coordinate neighborCoord = coord.add(direction.getOffset());
                        if (isValidCoordinate(neighborCoord) && !filledTiles.contains(neighborCoord)) {
                            Cell neighborCell = cells[neighborCoord.row][neighborCoord.col];
                            if (neighborCell instanceof FillableCell) {
                                FillableCell neighborFillableCell = (FillableCell) neighborCell;
                                if (neighborFillableCell.getPipe().isPresent()) {
                                    Pipe neighborPipe = neighborFillableCell.getPipe().get();
                                    if (Arrays.asList(neighborPipe.getConnections()).contains(direction.getOpposite())) {
                                        neighborPipe.setFilled();
                                        newFilledTiles.add(neighborCoord);
                                    }
                                }
                            } else if (neighborCell instanceof TerminationCell) {
                                TerminationCell terminationCell = (TerminationCell) neighborCell;
                                if (terminationCell.type == TerminationCell.Type.SINK) {
                                    terminationCell.setFilled();
                                    newFilledTiles.add(neighborCoord);
                                }
                            }
                        }
                    }
                }
            }
        }

        filledTiles.addAll(newFilledTiles);
        prevFilledTiles = newFilledTiles.size();
        prevFilledDistance = distance;
    }

