    public Map(int rows, int cols) {
        this.rows = rows;
        this.cols = cols;
        this.cells = new Cell[rows][cols];
        // Initialize cells with default values (e.g., Wall or FillableCell)
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                this.cells[i][j] = new Wall(new Coordinate(i, j));
            }
        }
    }

    /**
