        List<Move> moves = new ArrayList<>();
        int x = source.x();
        int y = source.y();
        Configuration configuration = game.getConfiguration();
        int size = configuration.getSize();
        Place destination;

        // move upwards
        for (int i = 1; i < size; i++) {
            if (x - i < 0) break;
            destination = new Place(x - i, y);
            if (!game.getPiece(destination).isEmpty()) {
                if (i == 1) continue; // cannot move to the place if there is a piece in the way
                moves.add(new Move(source, destination));
                break;
            }
            moves.add(new Move(source, destination));
        }

        // move downwards
        for (int i = 1; i < size; i++) {
            if (x + i >= size) break;
            destination = new Place(x + i, y);
            if (!game.getPiece(destination).isEmpty()) {
                if (i == 1) continue; // cannot move to the place if there is a piece in the way
                moves.add(new Move(source, destination));
                break;
            }
            moves.add(new Move(source, destination));
        }

        // move left
        for (int i = 1; i < size; i++) {
            if (y - i < 0) break;
            destination = new Place(x, y - i);
            if (!game.getPiece(destination).isEmpty()) {
                if (i == 1) continue; // cannot move to the place if there is a piece in the way
                moves.add(new Move(source, destination));
                break;
            }
            moves.add(new Move(source, destination));
        }

        // move right
