        List<Move> moves = new ArrayList<>();
        for (int x = -2; x <= 2; x++) {
            for (int y = -2; y <= 2; y++) {
                if (Math.abs(x) + Math.abs(y) == 3 && Math.abs(x) != Math.abs(y)) {
                    int destX = source.x() + x;
                    int destY = source.y() + y;
                    if (game.getConfiguration().isWithinBoard(destX, destY)) {
                        Place destination = new Place(destX, destY);
                        Move move = new Move(source, destination);
                        if (game.getBoard()[destX][destY] == null || !this.getPlayer().equals(game.getBoard()[destX][destY].getPlayer())) {
                            moves.add(move);
