                        Move move = new Move(source, destination);
                        if (game.getPiece(destination) == null || game.getPiece(destination).getPlayer() != this.player) {
                            moves.add(move);
                        }
                    }
                }
            }
        }
        return moves.toArray(new Move[0]);
