     * facilitate programming.
     * This is VERY different from the coordinate used in console display.</strong>
     *
     * @param game           the current game object
     * @param availableMoves available moves for this player to choose from.
     * @return the chosen move
     */
    @Override
    public Move nextMove(Game game, Move[] availableMoves) {
        // TODO student implementation
        Move chosenMove = null;
        boolean validMove = false;
        while (!validMove) {
            System.out.println("Please input your move (e.g. a1->b3): ");
            Scanner scanner = new Scanner(System.in);
            String input = scanner.nextLine();
            String[] moveStr = input.split("->");
            if (moveStr.length != 2) {
                System.out.println("Invalid input format, please try again.");
                continue;
            }
            String sourceStr = moveStr[0];
            String destinationStr = moveStr[1];
            if (sourceStr.length() != 2 || destinationStr.length() != 2) {
                System.out.println("Invalid place format, please try again.");
                continue;
            }
            int sourceX = sourceStr.charAt(0) - 'a';
            int sourceY = Integer.parseInt(sourceStr.substring(1)) - 1;
            int destinationX = destinationStr.charAt(0) - 'a';
            int destinationY = Integer.parseInt(destinationStr.substring(1)) - 1;
            Place source = new Place(sourceX, sourceY);
            Place destination = new Place(destinationX, destinationY);
            for (Move move : availableMoves) {
                if (move.getSource().equals(source) && move.getDestination().equals(destination)) {
                    chosenMove = move;
                    validMove = true;
                    break;
                }
            }
            if (!validMove) {
                System.out.println("Invalid move, please try again.");
            }
        }
        return chosenMove;
    }
}

