import assignment.protocol.Move;
import assignment.protocol.Piece;
import assignment.protocol.Place;
import assignment.protocol.Player;

/**
 * The rule of moving of Archer, which is similar to the moving rule of cannon in Chinese chess.
 * Cannons move like chariots, any distance orthogonally without jumping, but can only capture by jumping a single piece of either colour along the path of attack.
 */
public class ArcherMoveRule implements Rule {
    @Override
    public boolean validate(Game game, Move move) {
        if (!(game.getPiece(move.getSource()) instanceof Archer)) {
            return true;
        }
        // get the piece at the source place
        Piece piece = game.getPiece(move.getSource());
        // get the piece at the destination place
        Piece destinationPiece = game.getPiece(move.getDestination());
        // check if the move is a non-capturing move
        if (destinationPiece == null) {
            // check if the move is a straight move
            if (Math.abs(move.getSource().x() - move.getDestination().x()) == Math.abs(move.getSource().y() - move.getDestination().y())) {
                // check if there is no piece in the straight path
                int dx = (move.getDestination().x() - move.getSource().x()) / Math.abs(move.getDestination().x() - move.getSource().x());
                int dy = (move.getDestination().y() - move.getSource().y()) / Math.abs(move.getDestination().y() - move.getSource().y());
                for (int i = 1; i < Math.abs(move.getSource().x() - move.getDestination().x()); i++) {
                    if (game.getPiece(new Place(move.getSource().x() + dx * i, move.getSource().y() + dy * i)) != null) {
                        return false;
                    }
                }
                return true;
            }
        }
        // check if the move is a capturing move
        else if (destinationPiece.getPlayer() != piece.getPlayer()) {
            // check if the move is a jumping move
            if (Math.abs(move.getSource().x() - move.getDestination().x()) == 2 || Math.abs(move.getSource().y() - move.getDestination().y()) == 2) {
                // check if there is no piece in the straight path
                int dx = (move.getDestination().x() - move.getSource().x()) / Math.abs(move.getDestination().x() - move.getSource().x());
                int dy = (move.getDestination().y() - move.getSource().y()) / Math.abs(move.getDestination().y() - move.getSource().y());
                for (int i = 1; i < Math.abs(move.getSource().x() - move.getDestination().x()); i++) {
                    if (game.getPiece(new Place(move.getSource().x() + dx * i, move.getSource().y() + dy * i)) != null) {
                        return false;
                    }
                }
                return true;
            }
        }
        return false;
    }

    @Override
    public String getDescription() {
        return "archer move rule is violated";
    }
}

