import assignment.protocol.Player;

/**
 * The rule of moving of Archer, which is similar to the moving rule of cannon in Chinese chess.
 * Cannons move like chariots, any distance orthogonally without jumping, but can only capture by jumping a single piece of either colour along the path of attack.
 */
public class ArcherMoveRule implements Rule {
    @Override
    public boolean validate(Game game, Move move) {
        if (!(game.getPiece(move.getSource()) instanceof Archer)) {
            return true;
        }
        // get the piece at destination place
        Piece pieceAtDestination = game.getPiece(move.getDestination());
        // get the piece at source place
        Piece pieceAtSource = game.getPiece(move.getSource());
        // get the difference of x and y coordinates
        int diffX = Math.abs(move.getDestination().x() - move.getSource().x());
        int diffY = Math.abs(move.getDestination().y() - move.getSource().y());

        // if moving horizontally or vertically and not capturing or moving to an empty place
        if ((diffX == 0 || diffY == 0) && (pieceAtDestination == null || pieceAtDestination.getPlayer().equals(pieceAtSource.getPlayer()))) {
            return true;
        }

        // if moving diagonally and capturing an enemy piece
        if (diffX == diffY && pieceAtDestination != null && !pieceAtDestination.getPlayer().equals(pieceAtSource.getPlayer())) {
            // check if there is no piece in between source and destination
            int deltaX = (move.getDestination().x() - move.getSource().x()) / diffX;
            int deltaY = (move.getDestination().y() - move.getSource().y()) / diffY;
            for (int i = 1; i < diffX; i++) {
                int x = move.getSource().x() + i * deltaX;
                int y = move.getSource().y() + i * deltaY;
                if (game.getPiece(x, y) != null) {
                    return false;
                }
            }
            return true;
        }

        return false;
