     *
     * @param game   the game object
     * @param source the current place of the piece
     * @return an array of available moves
     */
    @Override
    public Move[] getAvailableMoves(Game game, Place source) {
        List<Move> moves = new ArrayList<>();
        int x = source.x();
        int y = source.y();
        int size = game.getConfiguration().getSize();
        // check if the move is valid in the four directions
        if (x + 1 < size && game.getPiece(x + 1, y) == null) {
            moves.add(new Move(source, new Place(x + 1, y)));
        }
        if (x - 1 >= 0 && game.getPiece(x - 1, y) == null) {
            moves.add(new Move(source, new Place(x - 1, y)));
        }
        if (y + 1 < size && game.getPiece(x, y + 1) == null) {
            moves.add(new Move(source, new Place(x, y + 1)));
        }
        if (y - 1 >= 0 && game.getPiece(x, y - 1) == null) {
            moves.add(new Move(source, new Place(x, y - 1)));
        }
        // check if the move is valid in the diagonal directions
        if (x + 1 < size && y + 1 < size && game.getPiece(x + 1, y + 1) == null) {
            moves.add(new Move(source, new Place(x + 1, y + 1)));
        }
        if (x - 1 >= 0 && y - 1 >= 0 && game.getPiece(x - 1, y - 1) == null) {
            moves.add(new Move(source, new Place(x - 1, y - 1)));
        }
        if (x - 1 >= 0 && y + 1 < size && game.getPiece(x - 1, y + 1) == null) {
            moves.add(new Move(source, new Place(x - 1, y + 1)));
        }
        if (x + 1 < size && y - 1 >= 0 && game.getPiece(x + 1, y - 1) == null) {
            moves.add(new Move(source, new Place(x + 1, y - 1)));
        }
        // check if the move is valid in the L shape
        if (x + 2 < size && y + 1 < size && game.getPiece(x + 2, y + 1) == null && game.getPiece(x + 1, y) == null) {
            moves.add(new Move(source, new Place(x + 2, y + 1)));
        }
        if (x - 2 >= 0 && y - 1 >= 0 && game.getPiece(x - 2, y - 1) == null && game.getPiece(x - 1, y) == null) {
            moves.add(new Move(source, new Place(x - 2, y - 1)));
        }
        if (x - 2 >= 0 && y + 1 < size && game.getPiece(x - 2, y + 1) == null && game.getPiece(x - 1, y) == null) {
            moves.add(new Move(source, new Place(x - 2, y + 1)));
        }
        if (x + 2 < size && y - 1 >= 0 && game.getPiece(x + 2, y - 1) == null && game.getPiece(x + 1, y) == null) {
            moves.add(new Move(source, new Place(x + 2, y - 1)));
        }
        return moves.toArray(new Move[0]);
    }
}

