     * @param source the current place of the piece
     * @return an array of available moves
     */
    @Override
    public Move[] getAvailableMoves(Game game, Place source) {
        List<Move> moves = new ArrayList<>();
        int x = source.x();
        int y = source.y();
        int size = game.getConfiguration().getSize();
        // check if the move is out of boundary
        if (x - 2 < 0 || y - 1 < 0 || x + 2 >= size || y + 1 >= size) {
            return new Move[0];
        }
        // check if the move is blocked by the same color piece
        if (game.getPiece(x - 2, y - 1)!= null && game.getPiece(x - 2, y - 1).getPlayer().equals(player)) {
            return new Move[0];
        }
        if (game.getPiece(x - 2, y + 1)!= null && game.getPiece(x - 2, y + 1).getPlayer().equals(player)) {
            return new Move[0];
        }
        if (game.getPiece(x + 2, y - 1)!= null && game.getPiece(x + 2, y - 1).getPlayer().equals(player)) {
            return new Move[0];
        }
        if (game.getPiece(x + 2, y + 1)!= null && game.getPiece(x + 2, y + 1).getPlayer().equals(player)) {
            return new Move[0];
        }
        // check if the move is blocked by the opponent's piece
        if (game.getPiece(x - 1, y - 2)!= null &&!game.getPiece(x - 1, y - 2).getPlayer().equals(player)) {
            moves.add(new Move(source, x - 2, y - 1));
        }
        if (game.getPiece(x - 1, y + 2)!= null &&!game.getPiece(x - 1, y + 2).getPlayer().equals(player)) {
            moves.add(new Move(source, x - 2, y + 1));
        }
        if (game.getPiece(x + 1, y - 2)!= null &&!game.getPiece(x + 1, y - 2).getPlayer().equals(player)) {
            moves.add(new Move(source, x + 2, y - 1));
        }
        if (game.getPiece(x + 1, y + 2)!= null &&!game.getPiece(x + 1, y + 2).getPlayer().equals(player)) {
            moves.add(new Move(source, x + 2, y + 1));
        }
        return moves.toArray(new Move[0]);
    }
}

