package assignment.piece;

import assignment.piece.ArcherMoveRule;
import assignment.piece.FirstNMovesProtectionRule;
import assignment.piece.NilMoveRule;
import assignment.piece.OccupiedRule;
import assignment.piece.OutOfBoundaryRule;
import assignment.piece.Rule;
import assignment.piece.VacantRule;
import assignment.protocol.Configuration;
import assignment.protocol.Game;
import assignment.protocol.Move;
import assignment.protocol.Piece;
import assignment.protocol.Place;
import assignment.protocol.Player;

import java.util.*;
import java.util.function.IntFunction;
import java.util.function.Predicate;
import java.util.stream.Stream;

/**
 * Archer piece that moves similar to cannon in chinese chess.
 * Rules of move of Archer can be found in wikipedia (https://en.wikipedia.org/wiki/Xiangqi#Cannon).
 * <p>
 * <strong>Attention: If you want to implement Archer as the bonus task, you should remove "{@code throw new
 * UnsupportedOperationException();}" in the constructor of this class.</strong>
 *
 * @see <a href='https://en.wikipedia.org/wiki/Xiangqi#Cannon'>Wikipedia</a>
 */
public class Archer extends Piece {
    public Archer(Player player) {
        super(player);
        throw new UnsupportedOperationException(); // remove this line if you plan to implement Archer
    }

    @Override
    public char getLabel() {
        return 'A';
    }

    /**
     * Returns an array of moves that are valid given the current place of the piece.
     * Given the {@link Game} object and the {@link Place} that current knight piece locates, this method should
     * return ALL VALID {@link Move}s according to the current {@link Place} of this knight piece.
     * All the returned {@link Move} should have source equal to the source parameter.
     * <p>
     * Hint: you should consider corner cases when the {@link Move} is not valid on the gameboard.
     * Several tests are provided and your implementation should pass them.
     * <p>
     * <strong>Attention: Student should make sure all {@link Move}s returned are valid.</strong>
     *
     * @param game   the game object
     * @param source the current place of the piece
     * @return an array of available moves
     */
    @Override
    public Move[] getAvailableMoves(Game game, Place source) {
        List<Move> moves = new ArrayList<>();
        int x = source.x();
        int y = source.y();
        int size = game.getConfiguration().getSize();

        // check if the move is out of boundary
        if (x - 1 >= 0 && y - 2 >= 0) {
            moves.add(new Move(source, x - 1, y - 2));
        }
        if (x - 2 >= 0 && y - 1 >= 0) {
            moves.add(new Move(source, x - 2, y - 1));
        }
        if (x + 1 < size && y - 2 >= 0) {
            moves.add(new Move(source, x + 1, y - 2));
        }
        if (x + 2 < size && y - 1 >= 0) {
            moves.add(new Move(source, x + 2, y - 1));
        }
        if (x - 1 >= 0 && y + 2 < size) {
            moves.add(new Move(source, x - 1, y + 2));
        }
        if (x - 2 >= 0 && y + 1 < size) {
            moves.add(new Move(source, x - 2, y + 1));
        }
        if (x + 1 < size && y + 2 < size) {
            moves.add(new Move(source, x + 1, y + 2));
        }
        if (x + 2 < size && y + 1 < size) {
            moves.add(new Move(source, x + 2, y + 1));
        }

        // filter out the moves that are out of boundary or occupied
        List<Move> validMoves = new ArrayList<>();
        for (Move move : moves) {
            if (move.getDestination().x() < 0 || move.getDestination().x() >= size ||
                    move.getDestination().y() < 0 || move.getDestination().y() >= size) {
                continue;
            }
            Piece piece = game.getPiece(move.getDestination());
            if (piece == null) {
                validMoves.add(move);
            } else if (piece.getPlayer().equals(this.getPlayer())) {
                continue;
            } else {
                validMoves.add(move);
            }
        }

        // check if the move is protected by the first n moves rule
        int numMovesProtection = game.getConfiguration().getNumMovesProtection();
        if (numMovesProtection > 0) {
            int numMoves = game.getNumMoves();
            if (numMoves <= numMovesProtection) {
                for (Move move : validMoves) {
                    if (move.equals(game.getPreviousMove())) {
                        validMoves.remove(move);
                    }
                }
            }
        }

        return validMoves.toArray(new Move[0]);
    }
}

