                    int newX = move.getDestination().x();
                    int newY = move.getDestination().y();
                    // check if the move is out of boundary
                    if (newX < 0 || newX >= size || newY < 0 || newY >= size) {
                        return false;
                    }
                    // check if the move is blocked by an enemy piece
                    Piece blockingPiece = game.getPiece(newX, newY);
                    if (blockingPiece!= null && blockingPiece.getPlayer()!= this.player) {
                        return false;
                    }
                    // check if the move is blocked by a friendly piece
                    for (int i = -1; i <= 1; i++) {
                        for (int j = -1; j <= 1; j++) {
                            if (i == 0 && j == 0) {
                                continue;
                            }
                            int blockedX = newX + i;
                            int blockedY = newY + j;
                            if (blockedX >= 0 && blockedX < size && blockedY >= 0 && blockedY < size) {
                                Piece piece = game.getPiece(blockedX, blockedY);
                                if (piece!= null && piece.getPlayer() == this.player) {
                                    return false;
                                }
                            }
                        }
                    }
                    return true;
                })
               .toArray(Move[]::new);
    }
}

