            String input = scanner.nextLine();
            String[] parts = input.split("->");
            if (parts.length!= 2) {
                System.out.println("Invalid input. Please enter the move in the format of (source) -> (destination).");
                continue;
            }
            String sourceStr = parts[0].trim();
            String destinationStr = parts[1].trim();
            char sourceX = sourceStr.charAt(0);
            char sourceY = sourceStr.charAt(1);
            char destinationX = destinationStr.charAt(0);
            char destinationY = destinationStr.charAt(1);
            int sourceXIndex = Character.toLowerCase(sourceX) - 'a';
            int sourceYIndex = sourceY - '1';
            int destinationXIndex = Character.toLowerCase(destinationX) - 'a';
            int destinationYIndex = destinationY - '1';
            Place source = new Place(sourceXIndex, sourceYIndex);
            Place destination = new Place(destinationXIndex, destinationYIndex);
            for (Move availableMove : availableMoves) {
                if (availableMove.getSource().equals(source) && availableMove.getDestination().equals(destination)) {
