package assignment.piece;

import assignment.piece.Rule;
import assignment.protocol.Game;
import assignment.protocol.Move;
import assignment.protocol.Piece;
import assignment.protocol.Place;

/**
 * The rule that requires capturing piece is not allowed within the first certain number of moves.
 */
public class FirstNMovesProtectionRule implements Rule {
    /**
     * The number of moves within which capturing piece is not allowed.
     */
    private final int numProtectedMoves;

    public FirstNMovesProtectionRule(int numProtectedMoves) {
        this.numProtectedMoves = numProtectedMoves;
    }

    @Override
    public boolean validate(Game game, Move move) {
        int numMoves = game.getNumMoves();
        if (numMoves < this.numProtectedMoves) {
            // if the number of moves is less than the protected number, then it is allowed to capture
            return true;
        }
        // if the number of moves is greater than or equal to the protected number, then it is not allowed to capture
        Piece sourcePiece = game.getPiece(move.getSource());
        Piece destinationPiece = game.getPiece(move.getDestination());
        if (sourcePiece == null || destinationPiece == null) {
            // if there is no piece at source or destination, then it is allowed to capture
            return true;
        }
        if (sourcePiece.getPlayer() == destinationPiece.getPlayer()) {
            // if the source and destination pieces are of the same player, then it is allowed to capture
            return true;
        }
        // if the source and destination pieces are of different players, then it is not allowed to capture
        return false;
    }

    @Override
    public String getDescription() {
        return "Capturing piece in the first " + this.numProtectedMoves + " moves are not allowed";
    }
}

