package pa1.controller;

import pa1.model.Cell;
import pa1.model.Direction;
import pa1.model.EntityCell;
import pa1.model.ExtraLife;
import pa1.model.GameBoard;
import pa1.model.Gem;
import pa1.model.Mine;
import pa1.model.MoveResult;
import pa1.model.Position;
import pa1.model.StopCell;

import java.util.*;

/**
 * Controller for {@link GameBoard}.
 *
 * <p>
 * This class is responsible for providing high-level operations to mutate a {@link GameBoard}. This should be the only
 * class which mutates the game board; Other classes should use this class to mutate the game board.
 * </p>
 */
public class GameBoardController {

    private final GameBoard gameBoard;

    /**
     * Creates an instance.
     *
     * @param gameBoard The instance of {@link GameBoard} to control.
     */
    public GameBoardController(final GameBoard gameBoard) {
        this.gameBoard = gameBoard;
    }

    /**
     * Moves the player in the given direction.
     *
     * <p>
     * You should ensure that the game board is only mutated if the move is valid and results in the player still being
     * alive. If the player dies after moving or the move is invalid, the game board should remain in the same state as
     * before this method was called.
     * </p>
     *
     * @param direction Direction to move the player in.
     * @return An instance of {@link MoveResult} representing the result of this action.
     */
    public MoveResult makeMove(final Direction direction) {
        Position playerPosition = gameBoard.getPlayer().getOwner().getPosition();
        Position newPosition = playerPosition.offsetByOrNull(direction.getRowOffset(), direction.getColOffset(), gameBoard.getNumRows(), gameBoard.getNumCols());
        if (newPosition == null) {
            return new MoveResult.Invalid(playerPosition);
        }

        EntityCell newCell = gameBoard.getEntityCell(newPosition);
        if (newCell == null) {
            return new MoveResult.Invalid(playerPosition);
        }

        if (newCell.getEntity() instanceof Mine) {
            return new MoveResult.Valid.Dead(playerPosition, newPosition);
        }

        List<Position> collectedGems = new ArrayList<>();
        List<Position> collectedExtraLives = new ArrayList<>();
        if (newCell.getEntity() instanceof Gem) {
            collectedGems.add(newPosition);
        } else if (newCell.getEntity() instanceof ExtraLife) {
            collectedExtraLives.add(newPosition);
        }

        gameBoard.getPlayer().getOwner().setEntity(null);
        gameBoard.getPlayer().setOwner(newCell);
        newCell.setEntity(gameBoard.getPlayer());

        return new MoveResult.Valid.Alive(newPosition, playerPosition, collectedGems, collectedExtraLives);
    }

    /**
     * Undoes a move by reverting all changes performed by the specified move.
     *
     * <p>
     * Hint: Undoing a move is effectively the same as reversing everything you have done to make a move.
     * </p>
     *
     * @param prevMove The {@link MoveResult} object to revert.
     */
    public void undoMove(final MoveResult prevMove) {
        if (prevMove instanceof MoveResult.Invalid) {
            return;
        }

        MoveResult.Valid validMove = (MoveResult.Valid) prevMove;
        Position newPosition = validMove.newPosition;
        Position origPosition = validMove.origPosition;

        EntityCell newCell = gameBoard.getEntityCell(newPosition);
        EntityCell origCell = gameBoard.getEntityCell(origPosition);

        newCell.setEntity(null);
        origCell.setEntity(gameBoard.getPlayer());
        gameBoard.getPlayer().setOwner(origCell);

        if (prevMove instanceof MoveResult.Valid.Alive) {
            MoveResult.Valid.Alive aliveMove = (MoveResult.Valid.Alive) prevMove;
            for (Position gemPosition : aliveMove.collectedGems) {
                gameBoard.getEntityCell(gemPosition).setEntity(new Gem());
            }

            for (Position extraLifePosition : aliveMove.collectedExtraLives) {
                gameBoard.getEntityCell(extraLifePosition).setEntity(new ExtraLife());
            }
        } else if (prevMove instanceof MoveResult.Valid.Dead) {
            MoveResult.Valid.Dead deadMove = (MoveResult.Valid.Dead) prevMove;
            gameBoard.getEntityCell(deadMove.minePosition).setEntity(new Mine());
        }
    }
}

