     * @return An instance of {@link MoveResult} representing the result of this action.
     */
    public MoveResult makeMove(final Direction direction) {
        Position newPosition = gameBoard.getPlayer().getPosition().offsetBy(direction.getRowOffset(), direction.getColOffset());
        EntityCell newCell = gameBoard.getEntityCell(newPosition);
        if (newCell == null) {
            return new MoveResult.Invalid(newPosition);
        }

        if (newCell.getEntity() instanceof Mine) {
            return new MoveResult.Valid.Dead(newPosition, newCell.getPosition());
        }

        List<Position> collectedGems = new ArrayList<>();
        List<Position> collectedExtraLives = new ArrayList<>();
        if (newCell.getEntity() instanceof Gem) {
            collectedGems.add(newCell.getPosition());
        } else if (newCell.getEntity() instanceof ExtraLife) {
            collectedExtraLives.add(newCell.getPosition());
        }

        Position oldPosition = gameBoard.getPlayer().getPosition();
        EntityCell oldCell = gameBoard.getEntityCell(oldPosition);
        oldCell.setEntity(null);
        gameBoard.getPlayer().setPosition(newPosition);
        newCell.setEntity(gameBoard.getPlayer());

        return new MoveResult.Valid.Alive(newPosition, oldPosition, collectedGems, collectedExtraLives);
